sys_ui_action.client_script_v2:
function onClick(g_form) {
    g_modal.showFields({
        title: "RFI Details",
        fields: [{
                type: 'choice',
                name: 'after_rfi',
                label: getMessage('After RFI'),
                choices: [{
                        label: '-- None --',
                        value: 'none'
                    },
                    {
                        label: 'Send Back ME',
                        value: 'send_back_me'
                    },
                    {
                        label: 'Start from Beginning',
                        value: 'start_from_beginning'
                    }
                ],
                mandatory: true
            },
            {
                type: 'choice',
                name: 'rfi_recipient',
                label: getMessage('RFI Recipient'),
                choices: [{
                        label: '-- None --',
                        value: 'none'
                    },
                    {
                        label: 'Applicant',
                        value: 'Applicant'
                    },
                    {
                        label: 'Pervious Aprover',
                        value: 'Pervious Aprover'
                    }
                ],
                mandatory: true
            },
            {
                type: 'reference',
                name: 'previous_approver',
                label: getMessage('Previous Approver'),
                reference: 'sysapproval_approver', // Correctly referencing Approval table
                mandatory: true
            },
            {
                type: 'string',
                name: 'approver',
                label: getMessage('Approver'),
                mandatory: true,
                readonly: true
            },
            {
                type: 'textarea',
                name: 'comment',
                label: getMessage('Comment'),
                mandatory: true
            }
        ],
        size: 'lg'
    }).then(function(fieldValues) {
        var ga = new GlideAjax('RFIApprovalHandler');
        ga.addParam('sysparm_name', 'handleRFIApproval');
        ga.addParam('sysparm_inc_parent', g_form.getUniqueValue());
        ga.addParam('sysparm_current_user', g_user.userID);
        ga.addParam('sysparm_inc_comments', getFieldValue(fieldValues, 'comment'));

        ga.getXMLAnswer(function(response) {});
    });
}
